"use strict";(globalThis.webpackChunkgsf_docusaurus_template=globalThis.webpackChunkgsf_docusaurus_template||[]).push([[2667],{6373(e){e.exports=JSON.parse('{"tag":{"label":"web","permalink":"/gsf-patterns/tags/web","allTagsPath":"/gsf-patterns/tags","count":11,"items":[{"id":"catalog/development/avoid-excessive-dom-size","title":"Avoid an excessive DOM size","description":"The greater the amount of nodes that are defined in HTML, the greater the time spent processing and rendering each element.","permalink":"/gsf-patterns/catalog/development/avoid-excessive-dom-size"},{"id":"catalog/development/avoid-chaining-critical-requests","title":"Avoid chaining critical requests","description":"Most web experiences require a lot of work from the user\'s browser. The greater the length of the chains and the larger the download sizes, the more significant the impact on page load performance and the energy required to render a page.","permalink":"/gsf-patterns/catalog/development/avoid-chaining-critical-requests"},{"id":"catalog/development/defer-offscreen-images","title":"Defer offscreen images","description":"Web pages offer a lot of images that aren\'t displayed on the first loaded screen and can thus be loaded dynamically.","permalink":"/gsf-patterns/catalog/development/defer-offscreen-images"},{"id":"catalog/development/enable-text-compression","title":"Enable text compression","description":"Web browsers often communicate with web servers in a human readable format. These can be HTML, JavaScript and/or CSS files and REST requests which can return a response in JSON. This human readable communication is redundant and, as such, can be compressed to save bandwidth.","permalink":"/gsf-patterns/catalog/development/enable-text-compression"},{"id":"catalog/development/keep-request-counts-low","title":"Keep request counts low","description":"Accessing a web page usually retrieves a HTML file from the web server. The HTML may then reference additional resources that the browser has to download.","permalink":"/gsf-patterns/catalog/development/keep-request-counts-low"},{"id":"catalog/development/minify-web-assets","title":"Minify web assets","description":"Minification removes unnecessary or redundant data without affecting how the resource is processed by the web browser.","permalink":"/gsf-patterns/catalog/development/minify-web-assets"},{"id":"catalog/development/minimize-main-thread-work","title":"Minimize main thread work","description":"Description","permalink":"/gsf-patterns/catalog/development/minimize-main-thread-work"},{"id":"catalog/development/optimize-image-delivery","title":"Optimize image delivery","description":"Modern image formats can help to reduce bandwidth, storage and computing requirements on the displaying device. Optimizing both static images and animated content significantly reduces data transfer and energy consumption.","permalink":"/gsf-patterns/catalog/development/optimize-image-delivery"},{"id":"catalog/development/properly-sized-images","title":"Optimize image size","description":"Ideally, the stored pixel dimensions are exactly the same, or smaller, as the display size in pixels so that no bandwidth or storage space is wasted.","permalink":"/gsf-patterns/catalog/development/properly-sized-images"},{"id":"catalog/development/remove-unused-css","title":"Remove unused CSS definitions","description":"CSS files are very complex and need energy intensive parsing and processing. Each added CSS definition increases the amount of time and processing power needed in this process.","permalink":"/gsf-patterns/catalog/development/remove-unused-css"},{"id":"catalog/development/use-server-side-rendering","title":"Use server-side rendering for high-traffic pages","description":"Use server-side rendering for high-traffic pages","permalink":"/gsf-patterns/catalog/development/use-server-side-rendering"}],"unlisted":false}}')}}]);